import flint
import math
import timeit
from fractalmath import *

###############################
# All these pairings of precision to count are based on the center
# of the Edge of Infinity point
###############################

#decimalPlaces = 16 
#mandelbrotMaxIter = 255
#timerRepeatCount = 1000

#decimalPlaces = 100
#mandelbrotMaxIter = 7000 
#timerRepeatCount = 100

decimalPlaces = 500
mandelbrotMaxIter = 35000 
timerRepeatCount = 10

#decimalPlaces = 1000
#mandelbrotMaxIter = 150000 
#timerRepeatCount = 10

#decimalPlaces = 1500
#mandelbrotMaxIter = 290000 
#timerRepeatCount = 10

#decimalPlaces = 2200
#mandelbrotMaxIter = 2700000 
#timerRepeatCount = 3


#centerString = '-1.7693831791+0.0042368479j'

centerString = '-1..'



pyMathSupport = DiveMathSupport()

flintMathSupport = DiveMathSupportFlint()
flintMathSupport.setPrecision(int(decimalPlaces*3.32))

flintCustomMathSupport = DiveMathSupportFlintCustom()
flintCustomMathSupport.setPrecision(int(decimalPlaces*3.32))

decMathSupport = DiveMathSupportDecimal()
decMathSupport.setPrecision(decimalPlaces)

def pyMandelbrotter():
    theCenter = pyMathSupport.createComplex(centerString)
    radius = 2.0
    return pyMathSupport.mandelbrot(theCenter, radius, mandelbrotMaxIter)

def flintMandelbrotter():
    theCenter = flintMathSupport.createComplex(centerString)
    radius = 2.0
    return flintMathSupport.mandelbrot(theCenter, radius, mandelbrotMaxIter)

def flintCustomMandelbrotter():
    theCenter = flintCustomMathSupport.createComplex(centerString)
    radius = 2.0
    return flintCustomMathSupport.mandelbrot(theCenter, radius, mandelbrotMaxIter)

def flintBeginningMandelbrotter():
    theCenter = flintCustomMathSupport.createComplex(centerString)
    radius = 2.0
    return flintCustomMathSupport.mandelbrot_beginning(theCenter, radius, mandelbrotMaxIter)

def arbMandelbrotter():
    theCenter = flintCustomMathSupport.createComplex(centerString)
    radius = 2.0
    return flintCustomMathSupport.mandelbrot_arb(theCenter, radius, mandelbrotMaxIter)

def mpfrMandelbrotter():
    theCenter = flintCustomMathSupport.createComplex(centerString)
    radius = 2.0
    return flintCustomMathSupport.mandelbrot_mpfr(theCenter, radius, mandelbrotMaxIter)

def decMandelbrotter():
    theCenter = decMathSupport.createComplex(centerString)
    radius = 2.0
    return decMathSupport.mandelbrot(theCenter, radius, mandelbrotMaxIter)

def main():
#    print("pure python (incorrect when iter > 100)")
#    print(timeit.Timer(pyMandelbrotter).timeit(number= timerRepeatCount))
    print("flint")
    print(timeit.Timer(flintMandelbrotter).timeit(number= timerRepeatCount))
    print("flint custom")
    print(timeit.Timer(flintCustomMandelbrotter).timeit(number= timerRepeatCount))
    print("flint low magnification")
    print(timeit.Timer(flintBeginningMandelbrotter).timeit(number= timerRepeatCount))
    print("arb steps")
    print(timeit.Timer(arbMandelbrotter).timeit(number= timerRepeatCount))
    print("mpfr steps")
    print(timeit.Timer(mpfrMandelbrotter).timeit(number= timerRepeatCount))
#    print("decimal")
#    print(timeit.Timer(decMandelbrotter).timeit(number= timerRepeatCount))

if __name__ == '__main__':
    main()

